import request from './request';

export interface UserInfo {
  id: number;
  name: string;
  email?: string;
  // 其他字段可自行扩展
}

export function getUserInfo(): Promise<UserInfo> {
  return request({
    url: '/user/info',
    method: 'get',
  });
}
