import request from './request';

export interface UserInfo {
  id: number;
  name: string;
  email?: string;
  // 其他字段可自行扩展
}
export interface RecordItem {
  id: number;
  name: string;
  time: string;
  // 其他字段
}
export function getUserInfo(): Promise<UserInfo> {
  return request({
    url: '/user/info',
    method: 'get',
  });
}

export function getLogs(): Promise<RecordItem> {
  return request({
    url: '/twitterInteractions/log',
    method: 'get',
  });
}
