import React, { useState, useEffect } from 'react';
import { Layout, List, Tabs, Table, Spin, message } from 'antd';
import './app.less';
import { getLogs } from './api/user'; // 确保路径正确

const { Sider, Content } = Layout;

interface RecordItem {
  id: number;
  name: string;
  time: string;
  // 你可以加其他需要的字段
}

interface QueryResult {
  // ...你的查询数据结构
}

interface CommentResult {
  // ...你的评论数据结构
}

const App: React.FC = () => {
  const [records, setRecords] = useState<RecordItem[]>([]);
  const [selectedId, setSelectedId] = useState<number | null>(null);
  const [recordsLoading, setRecordsLoading] = useState(true);

  const [queryResults, setQueryResults] = useState<QueryResult[]>([]);
  const [commentResults, setCommentResults] = useState<CommentResult[]>([]);
  const [loadingQuery, setLoadingQuery] = useState(false);
  const [loadingComment, setLoadingComment] = useState(false);

  // 获取执行记录
  useEffect(() => {
    setRecordsLoading(true);
    getLogs()
      .then((data: any) => {
        // 假设 data 是数组，字段名按后端实际调整
        setRecords(data);
        setRecordsLoading(false);
        if (data.length > 0) {
          setSelectedId(data[0].id); // 默认选中第一个
        }
      })
      .catch(() => {
        message.error('获取执行记录失败');
        setRecordsLoading(false);
      });
  }, []);

  // 获取查询、评论结果
  useEffect(() => {
    if (!selectedId) return;
    setLoadingQuery(true);
    setLoadingComment(true);

    // TODO: 替换成你的实际接口
    // fetchQueryResults(selectedId)
    //   .then(setQueryResults)
    //   .catch(() => message.error('查询结果获取失败'))
    //   .finally(() => setLoadingQuery(false));

    // fetchCommentResults(selectedId)
    //   .then(setCommentResults)
    //   .catch(() => message.error('评论结果获取失败'))
    //   .finally(() => setLoadingComment(false));
    setLoadingQuery(false); // mock
    setLoadingComment(false); // mock
  }, [selectedId]);

  // ...你的 queryColumns 和 commentColumns 不变...

  return (
    <Layout style={{ background: '#fff', minHeight: 500 }}>
      <Sider width={240} style={{ background: '#fff', borderRight: '1px solid #f0f0f0' }}>
        {recordsLoading ? (
          <Spin style={{ marginTop: 40 }} />
        ) : (
          <List
            header={<div style={{ fontWeight: 'bold' }}>执行记录</div>}
            dataSource={records}
            renderItem={item => (
              <List.Item
                style={{
                  background: item.id === selectedId ? '#e6f7ff' : undefined,
                  cursor: 'pointer',
                  fontWeight: item.id === selectedId ? 'bold' : undefined,
                }}
                onClick={() => setSelectedId(item.id)}
              >
                <div>
                  <div>{item.name || item.id}</div>
                  <div style={{ color: '#999', fontSize: 12 }}>{item.time}</div>
                </div>
              </List.Item>
            )}
          />
        )}
      </Sider>
      <Content style={{ padding: 24 }}>
        <Tabs defaultActiveKey="query">
          <Tabs.TabPane tab="查询结果" key="query">
            <Table
              rowKey="id"
              columns={queryColumns}
              dataSource={queryResults}
              size="small"
              pagination={false}
              loading={loadingQuery}
            />
          </Tabs.TabPane>
          <Tabs.TabPane tab="评论结果" key="comment">
            <Table
              rowKey="id"
              columns={commentColumns}
              dataSource={commentResults}
              size="small"
              pagination={false}
              loading={loadingComment}
            />
          </Tabs.TabPane>
        </Tabs>
      </Content>
    </Layout>
  );
};

export default App;
